import React, { useState, useEffect } from "react";
import './App.css';
import Axios from "axios";

function App() {

  const [id, setId] = useState('');
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [clientesList, setClientesList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/db/get").then((response)=> {
      setClientesList(response.data);
      //console.log(response)
    });
  });

  const incluirCliente = () => {
    Axios.post("http://localhost:3001/db/insert", {
      id:id, 
      nome: nome, 
      telefone:telefone
    }).then(() => {
      alert("Cliente incluído");
    });
  };

  const alterarCliente = () => {
    Axios.put("http://localhost:3001/db/update", {
      id:id, 
      nome: nome, 
      telefone:telefone
    }).then(() => {
      alert("Cliente alterado");
    });
  };

  const excluirCliente = (idCliente) => {
     Axios.delete(`http://localhost:3001/db/delete/${idCliente}`);
     alert("Cliente excluído");
  };

  return (
    <div className="App">
      <h1>Innova Talk 2022</h1>
      <div className="form">
        <label>ID do Cliente:</label>
        <input type="text" name="id" onChange={(e)=> {
          setId(e.target.value)
        }}/>
        <label>Nome:</label>
        <input type="text" name="nome" onChange={(e)=> {
          setNome(e.target.value)
        }}/>
        <label>Telefone:</label>
        <input type="type" name="telefone" onChange={(e)=> {
          setTelefone(e.target.value)
        }}/>
        <button onClick={incluirCliente}>Enviar</button>

        {clientesList.map((val) => {
          return(
            <div className="card">
              <h1>{val.id} - {val.nome}</h1> 
              <p>Telefone: {val.telefone} </p>
              <button onClick={() => {excluirCliente(val.id)}}>Excluir</button>
              <input type="text" id="alterarInput"/>
              <button onClick={() => {alterarCliente([val.id, val.nome, val.telefone])}}>Alterar</button>
            </div>
          )
        })}
      </div>
     </div>
  );
}

export default App;
